mapper.insert:
INSERT INTO job_workflow_folder (
    project_id, name, description, parent_id, sort_order, created_by
) VALUES (
    #{projectId}, #{name}, #{description}, #{parentId}, #{sortOrder}, #{createdBy}
)

mapper.update:
UPDATE job_workflow_folder
SET name = #{name},
    description = #{description},
    parent_id = #{parentId},
    sort_order = #{sortOrder}
WHERE id = #{id}

mapper.select:
SELECT COUNT(*)
FROM job_workflow_folder
WHERE parent_id = #{parentId} AND name = #{name}
