mapper.update:
UPDATE job_dependency
SET is_deleted = 1
WHERE workflow_id = #{workflowId}

mapper.delete:
DELETE FROM job_dependency
WHERE workflow_id = #{workflowId}
  AND (job_id = #{jobId} OR parent_job_id = #{jobId})

mapper.select:
SELECT COUNT(1)
FROM job_dependency
WHERE workflow_id = #{workflowId}
  AND job_id = #{jobId}
  AND parent_job_id = #{parentJobId}
  AND is_deleted = 0

mapper.delete:
DELETE FROM job_dependency
WHERE workflow_id = #{workflowId}
  AND job_id = #{jobId}
  AND parent_job_id = #{parentJobId}
