mapper.select:
SELECT DISTINCT r.type
FROM roles r
INNER JOIN user_roles ur ON r.id = ur.role_id
WHERE ur.tenant_id = #{tenantId}
  AND r.tenant_id = #{tenantId}
  AND ur.user_id = #{userId}

SELECT DISTINCT p.code
FROM permissions p
WHERE p.id IN (
    SELECT rp.permission_id
    FROM role_permissions rp
    INNER JOIN user_roles ur ON rp.role_id = ur.role_id
    WHERE ur.tenant_id = #{tenantId}
      AND rp.tenant_id = #{tenantId}
      AND ur.user_id = #{userId}
)

SELECT u.id, u.tenant_id, u.username, u.password, u.nickname, u.email, u.phone, tu.status AS status, u.deleted, u.created_at, u.updated_at FROM users u INNER JOIN tenant_users tu ON tu.user_id = u.id WHERE tu.tenant_id = #{tenantId}
AND tu.status = #{status}
AND u.deleted = 0 ORDER BY u.id DESC
